const loginFormHandler=a=>{a.preventDefault();const b=document.querySelector("#name-login").value.trim(),c=document.querySelector("#password-login").value.trim();loginHandler(b,c)},loginHandler=async(a,b)=>{if(a&&b){const c=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({username:a,password:b}),headers:{"Content-Type":"application/json"}});c.ok?document.location.replace("/profile"):alert(c.statusText)}},signupFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#name-signup").value.trim(),c=document.querySelector("#password-signup").value.trim();if(b&&c){const a=await fetch("/api/users/signup",{method:"POST",body:JSON.stringify({username:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?loginHandler(b,c):alert(a.statusText)}};document.querySelector(".login-form").addEventListener("submit",loginFormHandler),document.querySelector(".signup-form").addEventListener("submit",signupFormHandler);